prompt --application/shared_components/navigation/lists/rbac_menu_list
begin
wwv_flow_api.create_list(
 p_id=>wwv_flow_api.id(24658703155064840)
,p_name=>'RBAC Menu List'
,p_list_type=>'SQL_QUERY'
,p_list_query=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with',
'u as (',
'  select to_number(v(''APP_CURRENT_USER_ID'')) as user_id from dual',
'),',
'is_admin as (',
'  select case',
'           when exists (',
'             select 1',
'             from app_user_roles ur, u',
'             where ur.user_id = u.user_id',
'               and ur.role_id = 1',
'           ) then 1 else 0',
'         end as flag',
'  from dual',
'),',
'allowed_pages as (',
'  select p.page_id,',
'         p.page_name,',
'         p.apex_page_no,',
'         p.module_name,',
'         nvl(p.icon_class, ''fa-file'') as icon_class',
'    from app_pages p, u, is_admin a',
'   where p.status = ''ACTIVE''',
'     and p.apex_page_no not in (101,102,999)',
'     and (',
'          a.flag = 1',
'          or exists (',
'              select 1',
'                from app_user_page_access upa',
'               where upa.user_id = u.user_id',
'                 and upa.page_id = p.page_id',
'          )',
'     )',
'),',
'nodes as (',
'  -- Home (you can also store Home in app_pages if you want)',
'  select 10 id, null parent_id, 10 seq,',
'         ''Home'' label,',
'         apex_page.get_url(p_page => 1) target,',
'         ''fa-home'' icon_class',
'  from dual',
'',
'  union all',
'',
'  -- Module headers (icons also from DB if you want, but here we set nice defaults)',
'  select 100 + dense_rank() over(order by module_name) id,',
'         null parent_id,',
'         100 + dense_rank() over(order by module_name) * 10 seq,',
'         module_name || '' Module'' label,',
'         null target,',
'         case upper(module_name)',
'           when ''ADMIN'' then ''fa-user-lock''',
'           when ''KAIZEN'' then ''fa-analytics''',
'           when ''EXCEPTION'' then ''fa-files-o''',
'           else ''fa-folder''',
'         end icon_class',
'  from (select distinct module_name from allowed_pages)',
'',
'  union all',
'',
'  -- Child pages (ICON IS FROM YOUR DATABASE COLUMN p.icon_class)',
'  select 1000 + p.page_id id,',
'         100 + dense_rank() over(order by p.module_name) parent_id,',
'         1000 + p.apex_page_no seq,',
'         p.page_name label,',
'         apex_page.get_url(p_page => p.apex_page_no) target,',
'         p.icon_class',
'  from allowed_pages p',
'',
'  union all',
'',
'  -- Logout',
'  select 99999 id, null parent_id, 99999 seq,',
'         ''Logout'' label,',
'         apex_page.get_url(p_page => 999) target,',
'         ''fa-person-running-fast'' icon_class',
'  from dual',
')',
'select',
'  level as list_level,',
'  label,',
'  target,',
'  case when target is not null and :APP_PAGE_ID = :APP_PAGE_ID then ''NO'' end as is_current,',
'  icon_class as icon_css_classes,',
'  icon_class as attribute1',
'from nodes',
'start with parent_id is null',
'connect by prior id = parent_id',
'order siblings by seq;',
''))
,p_list_status=>'PUBLIC'
);
end;
