wwv_flow_api.create_list(
 p_id=>wwv_flow_api.id(24660748966235826)
,p_name=>'APP_USER_PAGE_ACCESS'
,p_list_type=>'SQL_QUERY'
,p_list_query=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT',
'    p.PAGE_ID AS entry_id,  -- numeric ID (first column)',
'    p.PAGE_NAME AS label,',
'    ''f?p='' || :APP_ID || '':'' || p.APEX_PAGE_NO || '':'' || :APP_SESSION AS target,',
'    NULL AS is_current,',
'    NULL AS image,',
'    NULL AS image_attribute,',
'    NULL AS attribute1,',
'    NULL AS attribute2,',
'    NULL AS attribute3,',
'    NULL AS attribute4,',
'    NULL AS attribute5',
'FROM APP_PAGES p',
'JOIN APP_USER_PAGE_ACCESS upa',
'  ON upa.PAGE_ID = p.PAGE_ID',
'JOIN APP_USERS u',
'  ON u.USER_ID = upa.USER_ID',
'WHERE p.STATUS = ''ACTIVE''',
'  AND UPPER(u.USERNAME) = UPPER(:APP_USER)',
'ORDER BY p.MODULE_NAME, p.PAGE_NAME;',
''))
,p_list_status=>'PUBLIC'
);
end;
