'BEGIN',
'  IF l_user_id IS NULL THEN',
'    RETURN;',
'  END IF;',
'',
'  ----------------------------------------------------------------------',
'  -- 1. Delete existing grants for this user (Admin, Kaizen, Exception, Travel)',
'  ----------------------------------------------------------------------',
'  DELETE FROM APP_USER_PAGE_ACCESS upa',
'   WHERE upa.USER_ID = l_user_id',
'     AND upa.PAGE_ID IN (',
