'',
'  ----------------------------------------------------------------------',
'  -- 2. Insert Admin pages (G_F01)',
'  ----------------------------------------------------------------------',
'  IF APEX_APPLICATION.G_F01.COUNT > 0 THEN',
'    FOR i IN 1 .. APEX_APPLICATION.G_F01.COUNT LOOP',
'      IF APEX_APPLICATION.G_F01(i) IS NOT NULL THEN',
'        INSERT INTO APP_USER_PAGE_ACCESS (USER_ID, PAGE_ID, GRANTED_AT)',
'        VALUES (l_user_id, APEX_APPLICATION.G_F01(i), SYSDATE);',
