'  -- 4. Insert Exception pages (G_F03)',
'  ----------------------------------------------------------------------',
'  IF APEX_APPLICATION.G_F03.COUNT > 0 THEN',
'    FOR i IN 1 .. APEX_APPLICATION.G_F03.COUNT LOOP',
'      IF APEX_APPLICATION.G_F03(i) IS NOT NULL THEN',
'        INSERT INTO APP_USER_PAGE_ACCESS (USER_ID, PAGE_ID, GRANTED_AT)',
'        VALUES (l_user_id, APEX_APPLICATION.G_F03(i), SYSDATE);',
'      END IF;',
'    END LOOP;',
'  END IF;',
'',
'  ----------------------------------------------------------------------',
'  -- 5. Insert Travel Request Module pages (G_F04)',
'  ----------------------------------------------------------------------',
'  IF APEX_APPLICATION.G_F04.COUNT > 0 THEN',
'    FOR i IN 1 .. APEX_APPLICATION.G_F04.COUNT LOOP',
'      IF APEX_APPLICATION.G_F04(i) IS NOT NULL THEN',
